document.addEventListener("DOMContentLoaded",()=>{var filterTrigger=document.getElementById("card-image");function resizeFilter(){var source=document.querySelector("#card-image img"),filter=document.getElementById("filter");filter.style.top=source.y+"px",filter.style.width=source.width+"px",filter.style.height=source.height+"px"}filterTrigger&&(filterTrigger.addEventListener("click",e=>{e.preventDefault(),resizeFilter()}),filterTrigger.addEventListener("mouseover",e=>{e.preventDefault(),resizeFilter()}))});